query:
query RealmQuery(
  $path: String!
) {
  ...UserData
  realm: realmByPath(path: $path) {
    id
    name
    path
    isMainRoot
    isUserRealm
    ownerDisplayName
    children {
      id
    }
    blocks {
      __typename
      id
    }
    canCurrentUserEdit
    ancestors {
      name
      path
      ownerDisplayName
      id
    }
    parent {
      id
    }
    ...BlocksData
    ...NavigationData
  }
}

fragment BlocksBlockData on Block {
  __isBlock: __typename
  id
  __typename
  ... on TitleBlock {
    ...TitleBlockData
  }
  ... on TextBlock {
    ...TextBlockData
  }
  ... on SeriesBlock {
    ...SeriesBlockData
  }
  ... on VideoBlock {
    ...VideoBlockData
  }
}

fragment BlocksData on Realm {
  ...BlocksRealmData
  blocks {
    __typename
    id
    ...BlocksBlockData
  }
}

fragment BlocksRealmData on Realm {
  path
}

fragment NavigationData on Realm {
  id
  name
  childOrder
  isUserRoot
  children {
    id
    name
    path
  }
  parent {
    isMainRoot
    name
    path
    id
  }
}

fragment SeriesBlockData on SeriesBlock {
  series {
    ...SeriesBlockSeriesData
    id
  }
  showTitle
  showMetadata
  order
}

fragment SeriesBlockSeriesData on Series {
  title
  syncedData {
    description
  }
  events {
    id
    title
    created
    creators
    isLive
    description
    syncedData {
      duration
      thumbnail
      startTime
      endTime
      tracks {
        resolution
      }
    }
  }
}

fragment TextBlockData on TextBlock {
  content
}

fragment TitleBlockData on TitleBlock {
  content
}

fragment UserData on Query {
  currentUser {
    username
    displayName
    canUpload
    canUseStudio
    canUseEditor
    canCreateUserRealm
  }
}

fragment VideoBlockData on VideoBlock {
  event {
    __typename
    ... on NotAllowed {
      dummy
    }
    ... on AuthorizedEvent {
      id
      title
      isLive
      created
      syncedData {
        duration
        updated
        startTime
        endTime
        thumbnail
        tracks {
          uri
          flavor
          mimetype
          resolution
          isMaster
        }
        captions {
          uri
          lang
        }
      }
    }
    ... on Node {
      __isNode: __typename
      id
    }
  }
  showTitle
  showLink
}
