query VideoPageInRealmQuery(
  $id: ID!
  $realmPath: String!
) {
  ...UserData
  event: eventById(id: $id) {
    __typename
    ...VideoPageEventData
    ... on Node {
      __isNode: __typename
      id
    }
  }
  realm: realmByPath(path: $realmPath) {
    referencesVideo: references(id: $id)
    ...VideoPageRealmData
    ...NavigationData
    id
  }
}

fragment NavigationData on Realm {
  id
  name
  childOrder
  isUserRoot
  children {
    id
    name
    path
  }
  parent {
    isMainRoot
    name
    path
    id
  }
}

fragment SeriesBlockSeriesData on Series {
  title
  syncedData {
    description
  }
  events {
    id
    title
    created
    creators
    isLive
    description
    syncedData {
      duration
      thumbnail
      startTime
      endTime
      tracks {
        resolution
      }
    }
  }
}

fragment UserData on Query {
  currentUser {
    username
    displayName
    canUpload
    canUseStudio
    canUseEditor
    canCreateUserRealm
  }
}

fragment VideoPageEventData on Event {
  __isEvent: __typename
  __typename
  ... on NotAllowed {
    dummy
  }
  ... on AuthorizedEvent {
    id
    title
    description
    creators
    created
    isLive
    opencastId
    metadata
    canWrite
    syncedData {
      updated
      duration
      thumbnail
      startTime
      endTime
      tracks {
        uri
        flavor
        mimetype
        resolution
        isMaster
      }
      captions {
        uri
        lang
      }
    }
    series {
      id
      title
      ...SeriesBlockSeriesData
    }
  }
}

fragment VideoPageRealmData on Realm {
  name
  path
  isMainRoot
  ancestors {
    name
    path
    id
  }
}
